const parseCsvData = (csv: string): CClassTribDefinition[] => {
  const rows: CClassTribDefinition[] = [];
  let i = 0;
  
  // Skip header
  while (i < csv.length && csv[i] !== '\n') i++;
  if (i < csv.length && csv[i] === '\n') i++;

  while (i < csv.length) {
    const cells: string[] = [];
    let currentCell = '';
    let inQuotes = false;
    let rowFinished = false;

    while (i < csv.length) {
      const char = csv[i];
      const nextChar = i + 1 < csv.length ? csv[i+1] : '';
      
      if (inQuotes) {
        if (char === '"') {
          if (nextChar === '"') {
             currentCell += '"';
             i++;
          } else {
             inQuotes = false;
          }
        } else {
          currentCell += char;
        }
      } else {
        if (char === '"') {
          inQuotes = true;
        } else if (char === ',') {
          cells.push(currentCell);
          currentCell = '';
        } else if (char === '\n') {
          cells.push(currentCell);
          rowFinished = true;
          i++;
          break;
        } else if (char !== '\r') {
          currentCell += char;
        }
      }
      i++;
    }
    
    if (!rowFinished && (cells.length > 0 || currentCell.length > 0)) {
         cells.push(currentCell);
    }
    
    if (cells.length > 5) {
       rows.push({
          cst: cells[0],
          cClassTrib: cells[2],
          name: cells[3],
          description: cells[4],
          legalText: cells[5],
          legalBasis: cells[6],
          taxRateType: cells[7],
          pRedIBS: parseFloat(cells[8]?.replace(',', '.') || '0'),
          pRedCBS: parseFloat(cells[9]?.replace(',', '.') || '0'),
          indicators: {
            ind_gTribRegular: cells[10],
            ind_gCredPresOper: cells[11],
            ind_gMonoPadrao: cells[12]
          },
          link: cells[cells.length - 1]?.replace(/[\r\n]+/g, '').trim()
       });
    }
  }
  return rows;
}

export const REF_CCLASS_TRIB = parseCsvData(CSV_SOURCE);